// src/components/WebsocketClient.tsx
import React, { useEffect, useState } from 'react';
import Cookies from "universal-cookie";
import {Box, Container, Stack, TextField, Typography} from "@mui/material";
import MessageCard from "./MessageCard";
import IconButton from "@mui/material/IconButton";
import SendRoundedIcon from "@mui/icons-material/SendRounded";
import {useNavigate} from "react-router-dom";

interface Message {
    username: string;
    content: string;
}

const WebsocketClient: React.FC = () => {
    const [ws, setWs] = useState<WebSocket | null>(null);
    const [messages, setMessages] = useState<Message[]>([]);
    const [content, setContent] = useState('');
    const cookies = new Cookies();
    const accessToken = cookies.get('access-token');
    const navigate = useNavigate();
    const [auth, setAuth] = useState(false);

    useEffect(() => {
        // Fetch historical messages
        fetch('http://localhost:8000/history', {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                'Authorization': `Bearer ${accessToken}`
            },
        })
            .then(response => {
                if (response.status!==200) {
                    console.log('Network response was not ok');
                    setAuth(false);
                    console.log(response)
                    throw new Error("no auth")
                }
                return response.json();
            })
            .then(data => {
                setMessages(data);
                setAuth(true);


                initializeWebSocket();


                return () => {
                    websocket.close();
                };
            })
            .catch(error => {
                console.error("Failed to fetch historical messages:", error);
                if (!auth) {
                    setTimeout(() => navigate("/login"), 4000);
                }
                setAuth(false)
            });
    }, []);

    const initializeWebSocket = () => {
        const websocket = new WebSocket('ws://localhost:8000/message');
        websocket.onopen = () => console.log('WebSocket Connected');
        websocket.onmessage = (event) => {
            const message: Message = JSON.parse(event.data);
            setMessages(prevMessages => [...prevMessages, message]);
        };
        websocket.onclose = () => console.log('WebSocket Disconnected');
        setWs(websocket);
    };

    const sendMessage = () => {
        if (ws) {
            ws.send(JSON.stringify({ username: cookies.get('user'), content: content }));
            setContent('');
        }
    };

    if (!auth) {
        return <>
           <Box  sx={{
               mt: 20,
               display: "flex",
               flexDirection: "column",
               alignItems: "center",
           }}>
               <Typography variant="h5">You have not login! Redirecting to login page...</Typography>
           </Box>
        </>
    }

    return (
        <>
            <Box height={1000}
                 width={1000}
                 my={4}
                 display="flex"
                 alignItems="center"
                 gap={4}
            >

                <Stack spacing={2}>
                    {messages.map((msg, index) => (
                        <MessageCard userName={msg.username} time="a" content={msg.content}/>
                    ))}
                    <TextField
                        id="messageInput"
                        placeholder="Write here..."
                        multiline
                        autoFocus
                        value={content}
                        onChange={(e) => setContent(e.target.value)}
                        fullWidth
                    />
                    <IconButton
                        size="large" color="primary" aria-label="send a messgae"
                        onClick={sendMessage}
                    >
                        <SendRoundedIcon />
                    </IconButton>
                </Stack>
            </Box>

        </>
    )
};

export default WebsocketClient;
