import * as React from 'react';
import Card from '@mui/material/Card';
import CardHeader from '@mui/material/CardHeader';
import CardContent from '@mui/material/CardContent';
import CardActions from '@mui/material/CardActions';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import ThumbUpIcon from "@mui/icons-material/ThumbUp";
import ThumbDownIcon from "@mui/icons-material/ThumbDown";
import BackgroundLetterAvatars from "./BackgroundLetterAvatars";
import {useState} from "react";

interface MessageCardProps {
    userName: string;
    time: string,
    content: string,
    message_id: string,
    voteCount: string,
    websocket: WebSocket | null
}

export default function MessageCard({userName, time, content, message_id, voteCount, websocket}: MessageCardProps) {
    const handleVote = async (type: 'upvote' | 'downvote') => {
        if (websocket && websocket.readyState === WebSocket.OPEN) {
            const messageData = {
                vote_type: type,
                username: String(userName),
                message_id: message_id
            };
            websocket.send(JSON.stringify(messageData));
            console.log("Sent:", JSON.stringify(messageData));
            if (type === 'upvote') {

            } else {

            }
        } else {
            console.error("WebSocket is not open.");
        }
    };

    return (
        <Card sx={{maxWidth: 1000}} variant="outlined">
            <CardHeader
                avatar={
                    <BackgroundLetterAvatars userName={userName}/>
                }
                title={userName}
                subheader={time}
            />
            <CardContent>
                <Typography variant="body2" color="text.secondary">
                    {content}
                </Typography>
            </CardContent>
            <CardActions disableSpacing>
                <IconButton
                    aria-label="upvote" size="small"
                    onClick={() => handleVote('upvote')}
                >
                    {/*<ThumbUpIcon color={"primary" : "action"} />*/}
                </IconButton>
                <IconButton
                    aria-label="downvote" size="small"
                    onClick={() => handleVote('downvote')}
                >
                    {/*<ThumbDownIcon color={isDownVoted ? "primary" : "action"}/>*/}
                </IconButton>
            </CardActions>
        </Card>
    );
}
