import React, {useEffect, useState} from 'react';
import Cookies from "universal-cookie";
import {Box, Container, CssBaseline, Grid, Stack, TextField, Typography} from "@mui/material";
import MessageCard from "../components/MessageCard";
import IconButton from "@mui/material/IconButton";
import SendRoundedIcon from "@mui/icons-material/SendRounded";
import {useNavigate} from "react-router-dom";

interface Message {
    username: string;
    content: string;
    created_at: string,
    message_id: string,
    upvote: string
}

const WebsocketClient = () => {
    const [ws, setWs] = useState<WebSocket | null>(null);
    const [messages, setMessages] = useState<Message[]>([]);
    const [content, setContent] = useState('');
    const cookies = new Cookies();
    const accessToken = cookies.get('access-token');
    const navigate = useNavigate();
    const [auth, setAuth] = useState(false);

    const formatDate = (dateString: string): string => {
        const date = new Date(dateString);
        return date.toLocaleString();
    };

    useEffect(() => {
        let websocket;
        // Fetch historical messages
        fetch('http://localhost:8000/history', {
            method: "GET",
            headers: {
                "Content-Type": "application/json",
                'Authorization': `Bearer ${accessToken}`
            },
        })
            .then(response => {
                if (response.status !== 200) {
                    console.log('Network response was not ok');
                    setAuth(false);
                    console.log(response)
                    throw new Error("no auth")
                }
                return response.json();
            })
            .then((data: Message[]) => {

                console.log(data)
                setMessages(data);
                setAuth(true);
                websocket = new WebSocket('ws://localhost:8000/message');

                websocket.onopen = () => {
                    console.log('WebSocket Connected');
                };

                websocket.onmessage = (event) => {
                    const message: Message = JSON.parse(event.data);
                    setMessages((prevMessages) => [...prevMessages, message]);
                };

                websocket.onclose = () => console.log('WebSocket Disconnected');

                setWs(websocket);
            })
            .catch(error => {
                console.error("Failed to fetch historical messages:", error);
                if (!auth) {
                    setTimeout(() => navigate("/login"), 4000);
                }
                setAuth(false)
            });

        return () => {
            if (ws) {
                ws.close();
                console.log('WebSocket has been closed');
            }
        };
    }, [accessToken]);

    const sendMessage = () => {
        if (ws) {
            ws.send(JSON.stringify({username: cookies.get('user'), content: content}));
            setContent('');
        }
    };

    if (!auth) {
        return <>
            <Box sx={{
                mt: 20,
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
            }}>
                <Typography variant="h5">You have not login! Redirecting to login page...</Typography>
            </Box>
        </>
    }

    return (
        <>
            <Container sx={{m: 'auto'}}
            >
                <CssBaseline/>
                <Box style={{maxHeight: 600, overflow: 'auto'}}>
                    <Stack spacing={4}>
                        {messages?.map((msg, index) => (
                            <MessageCard userName={msg.username} time={formatDate(msg.created_at)}
                                         content={msg.content}/>
                        ))}
                    </Stack>
                </Box>

                    <Grid sx={{ m: 'auto' }} container spacing={2}>
                        <Grid>
                            <TextField
                                id="messageInput"
                                placeholder="Write here..."
                                multiline
                                autoFocus
                                value={content}
                                onChange={(e) => setContent(e.target.value)}
                                size="medium"
                                margin="normal"
                            />
                        </Grid>
                        <Grid item xs={2}>
                            <IconButton
                            size="large" color="primary" aria-label="send a messgae"
                            onClick={sendMessage}
                            >
                            <SendRoundedIcon/>
                        </IconButton>

                        </Grid>

                    </Grid>
            </Container>

        </>
    )
};

export default WebsocketClient;
