                <TextField
                    id="messageInput"
                    placeholder="Write here..."
                    multiline
                    autoFocus
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    fullWidth
                />
                <IconButton
                    size="large" color="primary" aria-label="send a messgae"
                    onClick={() => sendMessage(message)}
