const API_URL = "http://localhost:8000/";
export const sendMessage = (username: string, content: string) => {
    return fetch(API_URL + "message", {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
        },
        body: JSON.stringify({
            content,
            username
        }),
    });
};

export const getMessage = (username: string) => {
    return fetch(API_URL + "message", {
        method: "POST",
        headers: {
            "Content-Type": "application/json",
        },
        body: JSON.stringify({
            username
        }),
    });
};


export const websocketChat = () => {
    const ws = new WebSocket('ws://localhost:8000/ws');

}
