import React, {useEffect, useState} from 'react';
import Cookies from "universal-cookie";
import {Box, Container, CssBaseline, Grid, Stack, TextField, Typography} from "@mui/material";
import MessageCard from "../components/MessageCard";
import IconButton from "@mui/material/IconButton";
import SendRoundedIcon from "@mui/icons-material/SendRounded";
import {useNavigate} from "react-router-dom";

interface BaseMessage {
    message_id: string;
    username: string;
}

// interface VoteMessage extends BaseMessage {
//     vote_type: string;  // "upvote" or "downvote"
//     vote_count: number;
// }

interface StoredMessage extends BaseMessage {
    created_at: string;
    content: string;
    vote_count?: string;
}

type Message = VoteMessage | StoredMessage;

const WebsocketClient = () => {
    let [ws, setWs] = useState<WebSocket | null>(null);
    const [messages, setMessages] = useState<StoredMessage[]>([]);
    const [content, setContent] = useState('');
    const cookies = new Cookies();
    const accessToken = cookies.get('access-token');
    const navigate = useNavigate();
    const [auth, setAuth] = useState(false);

    const formatDate = (dateString: string): string => {
        const date = new Date(dateString);
        return date.toLocaleString();
    };




    const setupWebSocket = (websocket:WebSocket) => {
        websocket.onopen = () => console.log('WebSocket Connected');
        websocket.onmessage = (event) => {
            const incomingMessage: Message = JSON.parse(event.data);
            setMessages(prevMessages => {
                const existingIndex = prevMessages.findIndex(msg => msg.message_id === incomingMessage.message_id);
                if (existingIndex > -1) {
                    const newMessages = [...prevMessages];
                    newMessages[existingIndex] = {
                        ...prevMessages[existingIndex],
                        ...incomingMessage,
                        vote_count: incomingMessage.vote_count || prevMessages[existingIndex].vote_count
                    };
                    return newMessages;
                }
                return [...prevMessages, incomingMessage];
            });
        };
        websocket.onclose = () => console.log('WebSocket Disconnected');
        setWs(websocket);
    };

    const sendMessage = () => {
        if (ws && ws.readyState === WebSocket.OPEN) {
            const username = cookies.get('user') || '';
            const messageData = {
                username: String(username),
                content: content
            };
            ws.send(JSON.stringify(messageData));
            console.log("Sent:", JSON.stringify(messageData));
            setContent('');
        } else {
            console.error("WebSocket is not open.");
        }
    };

    if (!auth) {
        return <>
            <Box sx={{
                mt: 20,
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
            }}>
                <Typography variant="h5">You have not login! Redirecting to login page...</Typography>
            </Box>
        </>
    }

    return (
        <>
            <Container sx={{m: 'auto'}}
            >
                <CssBaseline/>
                <Box style={{maxHeight: 600, overflow: 'auto'}}>
                    <Stack spacing={4}>
                        {messages?.map((msg, index) => (
                            <MessageCard userName={msg.username} time={formatDate(msg.created_at)}
                                         content={msg.content} message_id={msg.message_id} websocket={ws} />
                        ))}
                    </Stack>
                </Box>

                    <Grid sx={{ m: 'auto' }} container spacing={2}>
                        <Grid>
                            <TextField
                                id="messageInput"
                                placeholder="Write here..."
                                multiline
                                autoFocus
                                value={content}
                                onChange={(e) => setContent(e.target.value)}
                                size="medium"
                                margin="normal"
                            />
                        </Grid>
                        <Grid item xs={2}>
                            <IconButton
                            size="large" color="primary" aria-label="send a messgae"
                            onClick={sendMessage}
                            >
                            <SendRoundedIcon/>
                        </IconButton>
                        </Grid>
                    </Grid>
            </Container>
        </>
    )
};

export default WebsocketClient;
