import React, {useEffect, useState} from 'react';
import Cookies from "universal-cookie";
import {Box, Container, CssBaseline, Grid, Stack, TextField, Typography} from "@mui/material";
import MessageCard from "../components/MessageCard";
import IconButton from "@mui/material/IconButton";
import SendRoundedIcon from "@mui/icons-material/SendRounded";
import {useNavigate} from "react-router-dom";

interface BaseMessage {
    message_id: string;
    username: string;
}

interface VoteCount {
    message_id: string;
    count: string;
}

interface VoteMessage extends BaseMessage {
    vote_type: string; // "upvote" or "downvote"
    success: boolean;
    vote_count: string;
}

interface StoredMessage extends BaseMessage {
    created_at: string;
    content: string;
    vote_count?: string;
}

type MessageData = VoteMessage | StoredMessage;

const WebsocketClient = () => {
    let [ws, setWs] = useState<WebSocket | null>(null);
    const [messages, setMessages] = useState<StoredMessage[]>([]);
    const [content, setContent] = useState('');
    const cookies = new Cookies();
    const accessToken = cookies.get('access-token');
    const navigate = useNavigate();
    const [auth, setAuth] = useState(false);

    const formatDate = (dateString: string): string => {
        const date = new Date(dateString);
        return date.toLocaleString();
    };

    const fetchData = async () => {
        try {
            const voteResponse = await fetch('http://localhost:8000/vote-count', {
                headers: {
                    "Authorization": `Bearer ${accessToken}`
                }
            });
            const messageResponse = await fetch('http://localhost:8000/history', {
                headers: {
                    "Authorization": `Bearer ${accessToken}`
                }
            });

            if (!voteResponse.ok || !messageResponse.ok) {
                throw new Error('Network response was not ok');
            }

            const voteCounts: VoteCount[] = await voteResponse.json() || [];
            const messages: StoredMessage[] = await messageResponse.json() || [];

            // Merge vote counts into messages
            const voteCountMap = new Map(voteCounts.map(vote => [vote.message_id, vote.count]));
            const mergedMessages = messages.map(message => ({
                ...message,
                vote_count: voteCountMap.get(message.message_id) || '0'
            })) as StoredMessage[];
            setMessages(mergedMessages);
            setAuth(true);
        } catch (error) {
            console.error("Failed to fetch data:", error);
            setAuth(false);
            navigate("/login");
        }
    };

    useEffect(() => {
        fetchData();
        setupWebSocket(new WebSocket("ws://localhost:8000/message"));
    }, [accessToken, navigate]);


    const setupWebSocket = (websocket:WebSocket) => {
        websocket.onopen = () => console.log('WebSocket Connected');
        websocket.onmessage = (event) => {
            const incomingMessage = JSON.parse(event.data) as MessageData;
            setMessages(prevMessages => {
                const existingIndex = prevMessages.findIndex(msg => msg.message_id === incomingMessage.message_id);
                if (existingIndex > -1) {
                    const updatedMessages = [...prevMessages];
                    updatedMessages[existingIndex] = {
                        ...prevMessages[existingIndex],
                        ...incomingMessage,
                        vote_count:  incomingMessage.vote_count == null ||  incomingMessage.vote_count === "0"
                            ? prevMessages[existingIndex].vote_count
                            : incomingMessage.vote_count
                    };
                    return updatedMessages;
                } else {
                    return [...prevMessages, incomingMessage as StoredMessage];
                }
            });

            // const voteUpdate = JSON.parse(event.data);
            //
            // setMessages(prevMessages => prevMessages.map(msg => {
            //     if (msg.message_id === voteUpdate.message_id) {
            //         return {
            //             ...msg,
            //             upvotes: voteUpdate.upvotes ?? msg.upvotes,
            //             downvotes: voteUpdate.downvotes ?? msg.downvotes
            //         };
            //     }
            //     return msg;
            // }));
        };
        websocket.onclose = () => console.log('WebSocket Disconnected');
        setWs(websocket);
    };

    const sendMessage = () => {
        if (ws && ws.readyState === WebSocket.OPEN) {
            const username = cookies.get('user') || '';
            const messageData = {
                username: String(username),
                content: content
            };
            ws.send(JSON.stringify(messageData));
            console.log("Sent:", JSON.stringify(messageData));
            setContent('');
        } else {
            console.error("WebSocket is not open.");
        }
    };

    if (!auth) {
        return <>
            <Box sx={{
                mt: 20,
                display: "flex",
                flexDirection: "column",
                alignItems: "center",
            }}>
                <Typography variant="h5">You have not login! Redirecting to login page...</Typography>
            </Box>
        </>
    }

    return (
        <>
            <Container sx={{m: 'auto'}}
            >
                <CssBaseline/>
                <Box style={{maxHeight: 600, overflow: 'auto'}}>
                    <Stack spacing={4}>
                        {messages?.map((msg, index) => (
                            <MessageCard userName={msg.username} time={formatDate(msg.created_at)}
                                         content={msg.content} message_id={msg.message_id} websocket={ws}/>
                        ))}
                    </Stack>
                </Box>

                    <Grid sx={{ m: 'auto' }} container spacing={2}>
                        <Grid>
                            <TextField
                                id="messageInput"
                                placeholder="Write here..."
                                multiline
                                autoFocus
                                value={content}
                                onChange={(e) => setContent(e.target.value)}
                                size="medium"
                                margin="normal"
                            />
                        </Grid>
                        <Grid item xs={2}>
                            <IconButton
                            size="large" color="primary" aria-label="send a messgae"
                            onClick={sendMessage}
                            >
                            <SendRoundedIcon/>
                        </IconButton>
                        </Grid>
                    </Grid>
            </Container>
        </>
    )
};

export default WebsocketClient;
